//Packages
import React , {useState} from "react";

import {StyleSheet,Text,View,TextInput,Alert,Paragraph} from "react-native";

//Components
import AsyncStorage from '@react-native-async-storage/async-storage';
//Services
import api from "../services/api";
import Logo from ''
import { Button } from "../components/Button";


export default function menu( {navigation} ) {

    async function signIn() {
        try {
          if (Email.trim() === "" || Password.trim() === "") {
            return;
          }
    
      
          const res = await api.post("/login", {
            email: Email,
            password: Password,
          });
    
          const token = res.data.token;
    
          await AsyncStorage.setItem("tokenGerado", token);
          
         
          navigation.navigate("Navigation");
        } catch (error) {
          console.warn(error);
        }
      }
    async function Cadastrar() {
        try {
          navigation.navigate("Cadastro");
        } catch (error) {
          console.warn(error);
        }
      }
    return(
  <View style={styles.container}>
 
  <Text>2 IRMÃOS</Text>
  <Paragraph>Os melhores orçamentos e sem precisar sair de casa!</Paragraph>
  <Button onPress={signIn}>Entrar</Button>
  <Button onPress={Cadastrar} >Cadastre-se</Button>
  </View>
    );
}
