            style={styles.formInput}
            placeholder="Cor"
            onChangeText={color => this.setState({ color })}
            required
            />
    </View>

    <View style={styles.form2}>
          <TextInput 
            style={styles.formInput}
            placeholder="Município"
            onChangeText={city => this.setState({ city })}
            required
